mapper.select:
select count(*) from
(select * from report


		sel = #{sel}


		and status = #{status}

)


		writer like '%'||#{keyword}||'%' or id like '%'||#{keyword}||'%' or detail like '%'||#{keyword}||'%'


		and writer like '%'||#{keyword}||'%'


		and id like '%'||#{keyword}||'%'


		and detail like '%'||#{keyword}||'%'
